DROP TABLE IF EXISTS book_author;DROP TABLE IF EXISTS book;DROP SEQUENCE IF EXISTS seq_book_id;DROP TABLE IF EXISTS publisher;DROP SEQUENCE IF EXISTS seq_publisher_id;DROP TABLE IF EXISTS author;DROP SEQUENCE IF EXISTS  seq_author_id;CREATE TABLE author (author_id INT, author_name VARCHAR(400), CONSTRAINT pk_author PRIMARY KEY (author_id));CREATE SEQUENCE seq_author_id minvalue 100 increment by 50;CREATE TABLE publisher (publisher_id INT,publisher_name VARCHAR(400),CONSTRAINT pk_publisher PRIMARY KEY (publisher_id));CREATE SEQUENCE seq_publisher_id minvalue 102;CREATE TABLE book (book_id INT,title VARCHAR(400),isbn13 VARCHAR(13),num_pages INT,publication_date DATE,publisher_id INT,CONSTRAINT pk_book PRIMARY KEY (book_id),CONSTRAINT fk_book_pub FOREIGN KEY (publisher_id) REFERENCES publisher (publisher_id));CREATE SEQUENCE seq_book_id minvalue 100;CREATE TABLE book_author (book_id INT,author_id INT,CONSTRAINT pk_bookauthor PRIMARY KEY (book_id, author_id),CONSTRAINT fk_ba_book FOREIGN KEY (book_id) REFERENCES book (book_id),CONSTRAINT fk_ba_author FOREIGN KEY (author_id) REFERENCES author (author_id));insert into AUTHOR(author_id, author_name)VALUES (42, 'N.K. Jemisin');insert into AUTHOR(author_id, author_name)VALUES(2112, 'Rick Yancey');insert into PUBLISHER(PUBLISHER_ID, publisher_name)VALUES (100, 'Hachette Book Group'),       (101, 'G.P. Putnam''s Sons Books for Young Readers');insert into BOOK(book_id, title, isbn13, num_pages, publication_date, publisher_id)VALUES (100, 'The Fifth Season', '9780316229302', 512, '2015-09-04', 100),       (101, 'The Obelisk Gate', '9780316229289', 448, '2016-09-16', 100);insert into BOOK_AUTHOR(book_id, author_id)VALUES (100, 42),       (101, 42);--     (102, 42);